    const tabIndex = options.tabIndex || 0;
    
    const pane = new UIContainer();
    
    pane.addChild(new UIPane({ fullscreen: false, fill: '0x212121', padding: 0, x: 0, y: 0, w: w, h: h }));
    
    const tabContainer = new UIContainer();
    tabContainer.x = 0;
